import { createSlice } from '@reduxjs/toolkit'
export const movesSlice = createSlice({
  name: 'moves',
  initialState: {
    totalMoves:0,
    badMoves:0
  },
  reducers: {
    incrementBadMoves: (state) => {
      state.badMoves += 1
    },
    incrementTotalMoves: (state) => {
        state.totalMoves += 1
    },
  },
})
export const { incrementBadMoves, incrementTotalMoves } = movesSlice.actions
export default movesSlice.reducer
