import React, { useState } from 'react';
import './Card.styles.css';
import { useDispatch, useSelector } from 'react-redux';
import { pushToSelected } from '../../redux/selectedSlice';


export default function Card({id,name}) {
    let [rotateAngle,setRotateAngle]=useState(0);
    
    let [idToRemove,setIdToRemove]=useState(0);
    const {selected}= useSelector((store)=>  store);
    const dispatch=useDispatch();

    
    const flipCard =()=>{
        if(rotateAngle===0){
            setRotateAngle(180);
        }else{
            setRotateAngle(0); 
        }
        
    }

    const checkMatching=(selected)=>{
        if(selected.length ===2){
            if(selected[0].id === selected[1].id){
                setIdToRemove(selected[0].id)
            }
        }
    }
       
    const cardHandle =()=>{
        flipCard();
        dispatch(pushToSelected(id))
        checkMatching(selected)
    }

    
  return (
    <div  className={card}  >
    <div onClick={()=>cardHandle()} className={ rotateAngle===180? 'rotate-180 flip-card-inner':'rotate-0 flip-card-inner'}>

        <div className="flip-card-front">
            <h1>hidden</h1>
        </div>

        <div className="flip-card-back">
            <img className='card-image' src={`https://robohash.org/${id}?set=set2`} />
            <h4>{name}</h4>
        </div>
    </div>
    </div>
     )
}
