import { createSlice } from '@reduxjs/toolkit';
export const badMovesReducer = createSlice({
    name: 'badMoves',
    initialState: {
      badMoves: 0,
    },
    reducers: {
      incrementBadMoves: (state) => {
        state.badMoves += 1
      }
    }
  })
export const { incrementBadMoves } = badMovesReducer.actions
export default badMovesReducer.reducer
