import React, { useEffect, useState } from 'react';
import './Card.styles.css';


export default function Card({id,name}) {
    
        
    let [rotateAngle,setRotateAngle]=useState(0);
 
    //check card position and rotate it
    const flipCard = function (e){
        console.log(e.targt);
        if(rotateAngle===180){
            setRotateAngle(0);
        }else{
            setRotateAngle(180); 
        }
        
    }

    // get all elements at position 180 in y direction
    const allRotateCards=function (){
        return Array.from(
            document.getElementsByClassName('rotate-180')
        );
        
    }
    
    const checkMatching=function (cards){
        console.log(cards.length);
        if(cards.length === 2){
            if(cards[0].id === cards[1].id){
                setTimeout(function(){
                    cards.map((card)=>card.classList.add("display-none"))
                }, 500);
            }
            setTimeout(function(){
                cards.map((card)=>card.classList.remove("rotate-180"))
                cards.map((card)=>card.classList.add("rotate-0"))
                setRotateAngle(0)
            }, 1000);
        }
    }
       
    const cardHandle = async function(e){
        await flipCard(e);
        let cards=  await allRotateCards();
        checkMatching(cards)
        
    }

  return (
    <div  className='card'  >
    <div onClick={(e)=>cardHandle(e)} id={id} className={ rotateAngle===0? 'rotate-0 flip-card-inner':'rotate-180 flip-card-inner'}>

        <div className="flip-card-front">
            
        </div>

        <div className="flip-card-back">
            <img className='card-image' src={`https://robohash.org/${id}?set=set2`} />
            <h4>{name}</h4>
        </div>
    </div>
    </div>
     )
}
