import React, { useEffect, useState } from 'react';
import './Card.styles.css';


export default function Card({id,name}) {
    
        
    let [rotateAngle,setRotateAngle]=useState(0);
 
    //check card position and rotate it
    const flipCard = function (e){
        console.log(e.target.parentElement);
        console.log(e.target.classList.value==='flip-card-front');
        if(e.target.classList.value==='flip-card-front'){
            e.target.parentElement.classList.remove("rotate-0")
            e.target.parentElement.classList.add("rotate-180")
        }else{
            e.target.parentElement.parentElement.classList.remove("rotate-180")
            e.target.parentElement.parentElement.classList.add("rotate-0") 
        }
        
    }

    // get all elements at position 180 in y direction
    const allRotateCards=function (){
        return Array.from(
            document.getElementsByClassName('rotate-180')
        );
        
    }
    
    const checkMatching=function (cards){
        if(cards.length === 2){

            if(cards[0].id === cards[1].id){
                setTimeout(function(){
                    cards.map((card)=>card.classList.add("display-none"))
                }, 500);
            }

            setTimeout(function(){
                cards.map((card)=>card.classList.remove("rotate-180"))
                cards.map((card)=>card.classList.add("rotate-0"))
                setRotateAngle(0)
            }, 1000);
        }
    }
       
    const cardHandle =  function(e){
         flipCard(e);
        let cards=   allRotateCards();
        checkMatching(cards)
    }

  return (
    <div  className='card'  >
    <div onClick={(e)=>cardHandle(e)} id={id} className= 'rotate-0 flip-card-inner'>

        <div className="flip-card-front">
            <img />
        </div>

        <div className="flip-card-back">
            <img className='card-image' src={`https://robohash.org/${id}?set=set2`} />
            <h4>{name}</h4>
        </div>
    </div>
    </div>
     )
}
