import { createSlice } from '@reduxjs/toolkit'
export const movesSlice = createSlice({
  name: 'moves',
  initialState: {
    appearCards:[],
    totalMoves:0,
  },
  reducers: {
    
    incrementTotalMoves: (state) => {
        state.totalMoves += 1
    },
    flipCard:(state,action) => {
      console.log(action.payload);
      const layerCard=action.payload.e.target.parentElement;
      const innerCard=layerCard.parentElement;
      if(layerCard.classList.value==='flip-card-front'){
            innerCard.classList.remove("rotate-0")
            innerCard.classList.add("rotate-180")
            const addCards = [...state.appearCards]
            addCards.push(action.payload.id)
            state.appearCards=[...addCards] 
      }else{
        innerCard.classList.remove("rotate-180")
        innerCard.classList.add("rotate-0") 
      } 
    },
    checkMatchingfunction (cards){
      if(cards.length === 2){
          dispatch(incrementTotalMoves())
          console.log(appearCards);
          if(cards[0].id === cards[1].id){
              setTimeout(function(){
                  cards.map((card)=>card.classList.add("display-none"))
              }, 500);
          }

          setTimeout(function(){
              cards.map((card)=>card.classList.remove("rotate-180"))
              cards.map((card)=>card.classList.add("rotate-360"))
          }, 1000);
      }
  }

  },
})
export const {  incrementTotalMoves,flipCard } = movesSlice.actions
export default movesSlice.reducer
