import React, { useState } from 'react';
import './Card.styles.css';
import { useDispatch, useSelector } from 'react-redux';
import {emptySelected, pushToSelected } from '../../redux/selectedSlice';


export default function Card({id,name}) {
    
        
    let [rotateAngle,setRotateAngle]=useState(0);
    let [idToRemove,setIdToRemove]=useState(0);

    const {selected}= useSelector((store)=>  store);
    const dispatch=useDispatch();

    
    const flipCard =()=>{
        if(rotateAngle===0){
            setRotateAngle(180);
        }else{
            setRotateAngle(0); 
        }
        
    }

    const checkMatching=(selected)=>{
        if(selected.length ==2){
            const allWithClass = Array.from(
                document.getElementsByClassName('rotate-180')
              );
            allWithClass.map((card)=>card.classList.remove("rotate-180"))
            console.log(selected);
            if(selected[0] === selected[1]){
                setIdToRemove(selected[0])
                dispatch(emptySelected())
            }else{
                dispatch(emptySelected())
            }
            setRotateAngle(0)
        }
    }
       
    const cardHandle =(id)=>{
        flipCard();
        dispatch(pushToSelected(id))
        checkMatching(selected)
        emptySelected
    }

  return (
    <div  className={idToRemove==id?'display-none':'card' }  >
    <div onClick={()=>cardHandle(id)} className={ rotateAngle===180? 'rotate-180 flip-card-inner':'rotate-0 flip-card-inner'}>

        <div className="flip-card-front">
            <h1>hidden</h1>
        </div>

        <div className="flip-card-back">
            <img className='card-image' src={`https://robohash.org/${id}?set=set2`} />
            <h4>{name}</h4>
        </div>
    </div>
    </div>
     )
}
