import { createSlice } from '@reduxjs/toolkit'
export const movesSlice = createSlice({
  name: 'moves',
  initialState: {
    appearCards:[{}],
    totalMoves:0,
  },
  reducers: {
    
    incrementTotalMoves: (state) => {
        state.totalMoves += 1
    },flipCard:(state) => {
        state.totalMoves += 1
    }
  },
})
export const {  incrementTotalMoves } = movesSlice.actions
export default movesSlice.reducer
