import React, { useEffect, useState } from 'react';
import './Result.styles.css';
import { useSelector } from 'react-redux';


export default function Result({cardsNumber}) {
 const {totalMoves} = useSelector((state) => state.movesSlice)
 let [calcPrecentage,setCalcPrecentage]=useState(false);

 // get all elements at position 180 in y direction
 const allCompleteCards=function (){
    return Array.from(
        document.getElementsByClassName('display-none')
    );  
}
useEffect(()=>{
    checkEnd()
},[])


const checkEnd=function(){
    let allCompleteCardsNumber=allCompleteCards()
    setCalcPrecentage(true)
    console.log(calcPrecentage);
    if (cardsNumber === allCompleteCardsNumber.length) {
        setCalcPrecentage(true)
        console.log(calcPrecentage);
    }
}

 let prec
 if(totalMoves !== 0 && totalMoves >= cardsNumber/2 ){
    checkEnd()
    prec=Math.ceil( ( 1 - ( (totalMoves-cardsNumber/2) / totalMoves) )*100);
 }
  return (
    <div  className='result'>
        <div class="wrapper">
            <div id="bottom-bar">
                <div class="bottom-bar-element">
                    Total Moves: {totalMoves}
                </div>
                <div class="bottom-bar-element">
                    precentage:  {prec !== undefined ? prec : <span><i class="fa-solid fa-spinner fa-spin"></i></span>}               
                </div>
                <div class="bottom-bar-element">
                    
                </div>
                <div class="bottom-bar-element">
                
                    
                </div>
            </div>
        </div>
    </div>
    )
}
