import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Cards.styles.css';
import Card from '../Card/Card.component';
import { incrementBadMoves } from '../../redux/badMovesReducer';
import { useSelector,useDispatch } from 'react-redux';

export default function Cards() {
    let [cards,setCards]=useState([]);
    

    useEffect(()=>{
        getCards()
    },[])
    

    //use it to random cards
    const sortRandomly = (array) => array.sort(() => Math.random() - 0.5);
    
    const cardsHandle=(cards)=>{
        // limit number of shapes
        let limitedCards=cards.slice(4)
        //duplicate my cards
        limitedCards.push(...limitedCards)
        //sort my data randomly
        let myRandomData=sortRandomly(limitedCards);
        //store my data
        setCards(myRandomData);
    }
    const getCards=async function () {
        try{
            const {data}=await axios.get('https://jsonplaceholder.typicode.com/users');
            cardsHandle(data)
        }catch (error) {
            console.log(error);
          }
        
    }

    const badMoves = useSelector((state) => state.badMovesReducer.badMoves)
    console.log(badMoves);
    const dispatch = useDispatch()
    //check card position and rotate it
    const flipCard = function (e){
        const layerCard=e.target.parentElement;
        const innerCard=layerCard.parentElement;
        if(layerCard.classList.value==='flip-card-front'){
            if(innerCard.classList.contains('rotate-360')){
                innerCard.classList.remove("rotate-360")
                dispatch(incrementBadMoves())
                console.log(badMoves);
            }else{
                innerCard.classList.remove("rotate-0")
            }
            innerCard.classList.add("rotate-180")
        }else{
            innerCard.classList.remove("rotate-180")
            innerCard.classList.add("rotate-360") 
        }
        
    }
    

    
  return (
    <div className="cards">
        {
            cards.map((card,index)=> <Card flipCard={flipCard} index={index} key={index} {...card} />)
        }
        
    </div>
  )
}
