import { useState } from 'react';
import './Card.styles.css';

export default function Card({name,id},ca) {
    let [rotateAngle,setRotateAngle]=useState(0);
    let [selected,setSelected]=useState([]);
   
    const flipCard =()=>{
        if(rotateAngle===0){
            setRotateAngle(180);
        }else{
            setRotateAngle(0); 
        }
        
    }
    const pushToSelected =(id)=>{
        if(selected.length<3){
            setSelected(selected.push(id))
        } 
    }
    const cardHandle =()=>{
        flipCard();
        pushToSelected(id)
        
    }
    return (
    <div className="card" >
            <div className={ rotateAngle==180? 'rotate-180 flip-card-inner':'rotate-0 flip-card-inner'}
                onClick={()=>cardHandle(id)}>

                <div className="flip-card-front">
                    <h1>hidden</h1>
                </div>

                <div className="flip-card-back">
                    <img className='card-image' src={`https://robohash.org/${id}?set=set2`} />
                    <h4>{name}</h4>
                </div>
            </div>
    </div>
    )
}
