import React, { useEffect, useState } from 'react';
import './Card.styles.css';


export default function Card({id,name}) {
    let [rotateAngle,setRotateAngle]=useState(0);
    let [selected,setSelected]=useState([]);





    const flipCard =()=>{
        if(rotateAngle===0){
            setRotateAngle(180);
        }else{
            setRotateAngle(0); 
        }
        
    }
    const pushToSelected =()=>{
        if(selected.length < 3){
            let addingCart=[...selected]
            addingCart.push(id)
            setSelected(addingCart)
            console.log(selected);
        } 
    }
    const cardHandle =()=>{
        flipCard();
        pushToSelected()
    }

    
  return (
    <div  className="card"  >
    <div onClick={()=>cardHandle()} className={ rotateAngle===180? 'rotate-180 flip-card-inner':'rotate-0 flip-card-inner'}>

        <div className="flip-card-front">
            <h1>hidden</h1>
        </div>

        <div className="flip-card-back">
            <img className='card-image' src={`https://robohash.org/${id}?set=set2`} />
            <h4>{name}</h4>
        </div>
    </div>
    </div>
     )
}
