import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Cards.styles.css';
import Card from '../Card/Card.component';

export default function Cards() {
    let [cards,setCards]=useState([]);
    

    useEffect(()=>{
        getCards()
    },[])
    

    //use it to random cards
    const sortRandomly = (array) => array.sort(() => Math.random() - 0.5);
    
    const cardsHandle=(cards)=>{
        // limit number of shapes
        let limitedCards=cards.slice(4)
        //duplicate my cards
        limitedCards.push(...limitedCards)
        //sort my data randomly
        let myRandomData=sortRandomly(limitedCards);
        //store my data
        setCards(myRandomData);
    }
    const getCards=async function () {
        try{
            const {data}=await axios.get('https://jsonplaceholder.typicode.com/users');
            cardsHandle(data)
        }catch (error) {
            console.log(error);
          }
        
    }

    
    let [rotateAngle,setRotateAngle]=useState(0);
    
    let [idToRemove,setIdToRemove]=useState(0);
    const {selected}= useSelector((store)=>  store);
    const dispatch=useDispatch();

    
    const flipCard =()=>{
        if(rotateAngle===0){
            setRotateAngle(180);
        }else{
            setRotateAngle(0); 
        }
        
    }

    const checkMatching=(selected)=>{
        if(selected.length ==2){
            console.log(selected);
            if(selected[0] === selected[1]){
                setIdToRemove(selected[0])
                dispatch(emptySelected())
            }else{
                dispatch(emptySelected())
            }
            setRotateAngle(0)
        }
    }
       
    const cardHandle =(id)=>{
        flipCard();
        dispatch(pushToSelected(id))
        checkMatching(selected)
    }

    
  return (
    <div className="cards">
        {
            cards.map((card,index)=> <Card cardHandle={cardHandle} key={index} {...card} rotateAngle={rotateAngle} />)
        }
        
    </div>
  )
}
