import React, { useState } from 'react';
import './Card.styles.css';
import { useDispatch, useSelector } from 'react-redux';
import {emptySelected, pushToSelected } from '../../redux/selectedSlice';


export default function Card({id,name}) {
    
        
    let [rotateAngle,setRotateAngle]=useState(0);


    const {selected}= useSelector((store)=>  store);
    const dispatch=useDispatch();

    
    const flipCard =()=>{
        if(rotateAngle===0){
            setRotateAngle(180);
        }else{
            setRotateAngle(0); 
        }
        
    }
    const allRotateCards=()=>{
        return Array.from(
            document.getElementsByClassName('rotate-180')
        );
        
    }
    const checkMatching=(selected)=>{
        const cards= allRotateCards();
        if(cards.length ==2){
            console.log(cards[1].id);
            if(cards[0].id === cards[1].id){
                allRotateCards.map((card)=>card.classList.add("display-none"))
  
            }else{
                dispatch(emptySelected())
            }
            setRotateAngle(0)
            cards.map((card)=>card.classList.remove("rotate-180"))
            
            console.log(selected);

            
        }
    }
       
    const cardHandle =(id)=>{
        flipCard();
        dispatch(pushToSelected(id))
        checkMatching(selected)
    }

  return (
    <div  className='card'  >
    <div onClick={()=>cardHandle(id)} id={id} className={ rotateAngle===180? 'rotate-180 flip-card-inner':'rotate-0 flip-card-inner'}>

        <div className="flip-card-front">
            <h1>hidden</h1>
        </div>

        <div className="flip-card-back">
            <img className='card-image' src={`https://robohash.org/${id}?set=set2`} />
            <h4>{name}</h4>
        </div>
    </div>
    </div>
     )
}
