import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Cards.styles.css';
import Card from '../Card/Card.component';
import Result from '../Result/Result.component';
import { useDispatch, useSelector } from 'react-redux'
import { incrementBadMoves ,incrementTotalMoves} from '../../redux/movesSlice';
import EndPage from '../EndPage/EndPage.component';


export default function Cards() {
    const {totalMoves} = useSelector((state) => state.movesSlice)
    let [cards,setCards]=useState([]);
    let [end,setEnd]=useState(false);
    

    useEffect(()=>{
        getCards()
        checkEnd()
    },[])

    useEffect(()=>{
        checkEnd()
    },[totalMoves])

    // get all elements which user complete them
    const finishedCards=function (){
        return Array.from(
            document.getElementsByClassName('rotate-180')
        ); 
    }

    console.log(finishedCards());
    const checkEnd=function(){
        const completedCards=finishedCards()
        if(completedCards !== null){
            if(cards.length===completedCards.length){
                setEnd(true)
            }else{
                setEnd(false)
            }
        }
        
    }

    //use it to random cards
    const sortRandomly = (array) => array.sort(() => Math.random() - 0.5);
    
    const cardsHandle=(cards)=>{
        // limit number of shapes
        let limitedCards=cards.slice(4)
        //duplicate my cards
        limitedCards.push(...limitedCards)
        //sort my data randomly
        let myRandomData=sortRandomly(limitedCards);
        //store my data
        setCards(myRandomData);
    }
    const getCards=async function () {
        try{
            const {data}=await axios.get('https://jsonplaceholder.typicode.com/users');
            cardsHandle(data)
        }catch (error) {
            console.log(error);
          }
        
    }


    //logic for games
  
    const dispatch= useDispatch()
    //check card position and rotate it
    const flipCard = function (e){
        dispatch(incrementTotalMoves())
        const layerCard=e.target.parentElement;
        const innerCard=layerCard.parentElement;

        if(layerCard.classList.value==='flip-card-front'){
            if(innerCard.classList.contains('rotate-360')){
                innerCard.classList.remove("rotate-360")
                dispatch(incrementBadMoves())
            }else{
                innerCard.classList.remove("rotate-0")
            }
            innerCard.classList.add("rotate-180")
        }else{
            innerCard.classList.remove("rotate-180")
            innerCard.classList.add("rotate-360") 
        }
        
    }
    
    // get all elements at position 180 in y direction
    const allRotateCards=function (){
        return Array.from(
            document.getElementsByClassName('rotate-180')
        );  
    }
    const checkMatching=function (cards){
        if(cards.length === 2){

            if(cards[0].id === cards[1].id){
                setTimeout(function(){
                    cards.map((card)=>card.classList.add("display-none"))
                }, 500);
            }

            setTimeout(function(){
                cards.map((card)=>card.classList.remove("rotate-180"))
                cards.map((card)=>card.classList.add("rotate-360"))
            }, 1000);
        }
    }
       
    const cardHandle =  function(e){
        flipCard(e);
        let cards=   allRotateCards();
        checkMatching(cards)
    }
  return (
    <div className="cards">
        {console.log('render')}
        <Result/>
        {   end ===false?
            cards.map((card,index)=> <Card cardHandle={cardHandle} index={index} key={index} {...card} />)
            :<EndPage />
        }
        
    </div>
  )
}
