import React, { useEffect, useState } from 'react';
import './Card.styles.css';


export default function Card({id,name}) {
    
        
    let [rotateAngle,setRotateAngle]=useState(0);


    useEffect(()=>{
        checkMatching();
        
    },)

    const flipCard =()=>{
        if(rotateAngle===0){
            setRotateAngle(180);
        }else{
            setRotateAngle(0); 
        }
        
    }
    const allRotateCards=()=>{
        return Array.from(
            document.getElementsByClassName('rotate-180')
        );
        
    }
    const checkMatching=()=>{
        const cards= allRotateCards();
        if(cards.length ==2){
            if(cards[0].id === cards[1].id){
                setInterval(function(){
                    cards.map((card)=>card.classList.add("display-none"))
                }, 500);
            }
            setInterval(function(){
                cards.map((card)=>card.classList.remove("rotate-180"))
            }, 1000);
        }
    }
       
    const cardHandle =()=>{
        flipCard();
        checkMatching()
    }

  return (
    <div  className='card'  >
    <div onClick={()=>cardHandle()} id={id} className={ rotateAngle===180? 'rotate-180 flip-card-inner':'rotate-0 flip-card-inner'}>

        <div className="flip-card-front">
            <h1>hidden</h1>
        </div>

        <div className="flip-card-back">
            <img className='card-image' src={`https://robohash.org/${id}?set=set2`} />
            <h4>{name}</h4>
        </div>
    </div>
    </div>
     )
}
